import React, { useEffect, useState } from "react";
import { getBalance } from "../api/client";

interface Transaction {
  id: string;
  timestamp: string;
  amount: number;
  type: number;
  description: string;
}

const TransactionList: React.FC = () => {
  const [transactions, setTransactions] = useState<Transaction[]>([]);
  const [balance, setBalance] = useState<number>(0);

  useEffect(() => {
    const accountId = "1";

    getBalance(accountId)
      .then(setBalance)
      .catch((err) => console.error("Failed to fetch balance", err));

    fetch(`http://localhost:5078/api/accounts/${accountId}/transactions`)
      .then((res) => res.json())
      .then((data) => {
        if (Array.isArray(data.items)) {
          setTransactions(data.items);
        } else {
          console.error("Unexpected transactions format:", data);
        }
      })
      .catch((err) => console.error("Failed to fetch transactions", err));
  }, []);

  return (
    <div>
      <h2>Account Balance: ${balance.toFixed(2)}</h2>
      <ul>
        {transactions.map((t) => (
          <li key={t.id}>
            {new Date(t.timestamp).toLocaleString()}: ${t.amount.toFixed(2)} -{" "}
            {t.description}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TransactionList;
