import axios from "axios";

const api = axios.create({
  baseURL: "http://localhost:5078/ledger",
});

export const getBalance = async (accountId: string): Promise<number> => {
  const response = await api.get(`/accounts/${accountId}/balance`);
  return response.data;
};

export const postTransaction = async (
  accountId: string,
  transaction: { amount: number; type: number; description: string }
): Promise<void> => {
  await api.post(`/accounts/${accountId}/transactions`, transaction);
};
