import React, { useEffect, useState } from "react";
import { getBalance, postTransaction } from "../api/client";

interface Transaction {
  id: string;
  timestamp: string;
  amount: number;
  type: number;
  description: string;
}

const TransactionList: React.FC = () => {
  const [transactions, setTransactions] = useState<Transaction[]>([]);
  const [balance, setBalance] = useState<number>(0);
  const [amount, setAmount] = useState<number>(0);
  const [type, setType] = useState<number>(0);
  const [description, setDescription] = useState<string>("");

  useEffect(() => {
    const accountId = "1";
    getBalance(accountId).then(setBalance);

    fetch(`http://localhost:5078/api/accounts/${accountId}/transactions`)
      .then((res) => res.json())
      .then((data) => setTransactions(data.items)); // handle pagination response
  }, []);

  const handleSubmit = async () => {
    try {
      await postTransaction("1", { amount, type, description });
      window.location.reload(); // optional: refresh to update the list
    } catch (error) {
      console.error("Failed to submit transaction", error);
    }
  };

  return (
    <div>
      <h2>Account Balance: ${balance}</h2>
      <ul>
        {transactions.map((t) => (
          <li key={t.id}>
            {t.timestamp}: {t.amount} - {t.description}
          </li>
        ))}
      </ul>

      <h3>Add Transaction</h3>
      <form
        onSubmit={(e) => {
          e.preventDefault();
          handleSubmit();
        }}
      >
        <input
          type="number"
          value={amount.toString()}
          onChange={(e) => setAmount(Number(e.target.value))}
          placeholder="Amount"
        />
        <select value={type} onChange={(e) => setType(Number(e.target.value))}>
          <option value={0}>Deposit</option>
          <option value={1}>Withdrawal</option>
        </select>
        <input
          type="text"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          placeholder="Description"
        />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default TransactionList;
