import React, { useEffect, useState } from "react";
import { getBalance, postTransaction } from "../api/client";

interface Transaction {
  id: string;
  timestamp: string;
  amount: number;
  type: string;
  description: string;
  accountId: string;
}

const TransactionList: React.FC = () => {
  const [transactions, setTransactions] = useState<Transaction[]>([]);
  const [balance, setBalance] = useState<number>(0);
  const [amount, setAmount] = useState<string>("");
  const [description, setDescription] = useState<string>("");
  const [type, setType] = useState<string>("Deposit");

  const accountId = "1";

  useEffect(() => {
    getBalance(accountId).then(setBalance);

    fetch(`http://localhost:5078/api/accounts/${accountId}/transactions`)
      .then((res) => res.json())
      .then((data) => setTransactions(data.items || []));
  }, []);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      await postTransaction(accountId, {
        amount: parseFloat(amount),
        description,
        type,
      });
      setAmount("");
      setDescription("");
      setType("Deposit");
      // Refresh transactions
      fetch(`http://localhost:5078/api/accounts/${accountId}/transactions`)
        .then((res) => res.json())
        .then((data) => setTransactions(data.items || []));
      getBalance(accountId).then(setBalance);
    } catch (error) {
      console.error("Failed to submit transaction", error);
    }
  };

  return (
    <div>
      <h2>Account Balance: ${balance}</h2>

      <form onSubmit={handleSubmit}>
        <input
          type="number"
          placeholder="Amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
        />
        <input
          type="text"
          placeholder="Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
        <select value={type} onChange={(e) => setType(e.target.value)}>
          <option value="Deposit">Deposit</option>
          <option value="Withdrawal">Withdrawal</option>
        </select>
        <button type="submit">Add Transaction</button>
      </form>

      <ul>
        {transactions.map((t) => (
          <li key={t.id}>
            {t.timestamp}: {t.amount} - {t.description} ({t.type})
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TransactionList;
